function ots_alert(e){$("#screen_console").append("<p>"+e+"</p>")}var output=document.getElementById("output");Leap.loop(function(e){var t=Leap.loop({enableGestures:!0},function(e){e.valid&&e.gestures.length>0&&e.gestures.forEach(function(e){switch(e.type){case"circle":ots_alert("Circle Gesture");break;case"keyTap":ots_alert("Key Tap Gesture");break;case"screenTap":ots_alert("Screen Tap Gesture");break;case"swipe":ots_alert("Swipe Gesture")}})})});